<?php 	$default = array(		'post_type' => 'product',		'orderby' => $orderby,		'order' => $order,		'meta_key'		=> 'recommend_product',		'meta_value'	=> 'yes',		'post_status' => 'publish',		'showposts' => $number	);$i=0;$list = new WP_Query( $default );ob_start();				if ( count($list) > 0 ){ ?>			<div class="panel-group accordion_recommend_product" id="accordion">				<?php if( $title != ''){ ?>			<div class="block-title">				<strong>					<span><?php echo $title ?></span>				</strong>				<div class="sn-img icon-bacsic2"></div>			</div>			<?php } ?>			<?php while($list->have_posts()): $list->the_post();global $product, $post, $wpdb, $average;			  $i++			?>		  <div class="panel panel-default">		  			<?php $att = ($i != '1') ? 'class="collapsed"': 'class=""'?>				<a data-toggle="collapse" data-parent="#accordion" href="#collapse<?php echo $i ?> " <?php echo $att?>>					<span class="item-count">						 <?php echo $i ?>					</span>					 <h4 class="panel-title">					 <?php the_title(); ?>					 </h4>				</a>			<?php $attributes = ($i == '1') ? 'class="panel-collapse collapse in"': 'class="panel-collapse collapse"'?>			<div id="collapse<?php echo $i ?>" <?php echo $attributes ?>>			  <div class="panel-body">						 <div class="item-content">							<?php if(has_post_thumbnail()){ ?>								<div class="item-img item-height">									<div class="item-img-info products-thumb">										<a href="<?php the_permalink();?>" title="<?php the_title_attribute();?>">											<?php echo woocommerce_get_product_thumbnail('thumbnail') ?>										</a>									</div>								</div>							<?php } ?>								<h4><a href="<?php the_permalink(); ?>" title="<?php the_title_attribute();?>"><?php the_title(); ?></a></h4>								<!-- rating  -->								<?php 									$rating_count = $product->get_rating_count();									$review_count = $product->get_review_count();									$average      = $product->get_average_rating();								?>								<div class="reviews-content">									<div class="star"><?php echo ( $average > 0 ) ?'<span style="width:'. ( $average*14 ).'px"></span>' : ''; ?></div>									<div class="item-number-rating">										<?php echo $review_count; _e(' Review(s)', 'shoppystore');?>									</div>								</div>									<!-- end rating  -->								<?php if ( $price_html = $product->get_price_html() ){?>								<div class="item-price">									<span>										<?php echo $price_html; ?>									</span>								</div>								<?php } ?>							</div>				  </div>			</div>		  </div>		<?php endwhile; wp_reset_query();?>	</div>		<?php } 